package rep1;

public class RelationalOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean isEqual = 20 == 21;// == Operator
		System.out.println("Boolean value is " + isEqual);
		
		boolean isNotEqual = 20 != 21;//!= Operator
		System.out.println("Boolean value is " + isNotEqual);
		
		boolean isGreater = 20 > 21;// > Operator
		System.out.println("Boolean value is " + isGreater);
		
		boolean islessThan = 20 < 21;// < Operator
		System.out.println("Boolean value is " + islessThan);
		
		boolean isGreaterEqual = 5 >= 5;// >= Operator
		System.out.println("Boolean value is " + isGreaterEqual);
		
		boolean lessThanEqual = 7 <= 6;// <= Operator
		System.out.println("Boolean value is " + lessThanEqual);
	}

}
